const endPoint = window.themeDisplay ? window.themeDisplay.getCDNBaseURL() + '/o/v2/mobilink/' : 'http://192.168.10.89:8080/api/'
const urlNewsBoards = endPoint + 'newsboards'

const hasClient = {

  getNewsBoards (query, cb, errorCb) {
    const result = {
      total: window._newsBoardItems.length,
      data: window._newsBoardItems
    }
    setTimeout(() => cb(result), 100)
  },

  readNewsBoard (newsBoardId, cb, errorCb) {
    setTimeout(() => cb(window._newsBoardItems.find(item => Number(item.newsBoardId) === Number(newsBoardId || 0))), 100)
  },

  createNewsBoard (params, cb, errorCb) {
    console.log(params)
    let result = {}
    for (let key in window._newsBoardItems[0]) {
      if (key === 'newsBoardId') {
        result[key] = Math.floor(Math.random() * 10000) + 1
      } else {
        result[key] = params[key] || window._newsBoardItems[0][key]
      }
    }
    // error window._newsBoardItems.push(result)
    setTimeout(() => cb(result), 100)
  },

  updateNewsBoard (params, cb, errorCb) {
    let updated = window._newsBoardItems.find(item => item.newsBoardId === params.newsBoardId)
    let result = {}
    Object.assign(result, updated, params)
    // error let index = window._newsBoardItems.findIndex(item => item.newsBoardId === params.newsBoardId)
    // error window._newsBoardItems[index] = result
    setTimeout(() => cb(result), 100)
  },

  deleteNewsBoard (newsBoardId, cb, errorCb) {
    window._newsBoardItems = window._newsBoardItems.filter(item => item.newsBoardId !== newsBoardId)
    setTimeout(() => cb(window._newsBoardItems.find(item => item.newsBoardId === newsBoardId)), 100)
  },

  downloadNewsBoard (newsBoardId, cb, errorCb) {
    window._newsBoardItems = window._newsBoardItems.filter(item => item.newsBoardId !== newsBoardId)
    setTimeout(() => cb(window._newsBoardItems.find(item => item.newsBoardId === newsBoardId)), 100)
  },

  copyNewsBoard (params, cb, errorCb) {
    setTimeout(() => cb(window._newsBoardItems), 100)
  }

}
const hasServer = {

  getNewsBoards (query, cb, errorCb) {
    let url = urlNewsBoards
    let queries = {
      start: -1,
      end: -1
    }
    Object.assign(queries, query)
    window.axios.get(url, {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      params: queries
    })
      .then(function (response) {
        const serializable = response.data
        let result = {
          total: serializable.total,
          data: [...serializable.data || []]
        }
        if (!serializable.data) {
          result.data = []
        } else if (serializable.data.length) {
          result.data = serializable.data
        } else {
          result.data = [serializable.data]
        }
        cb(result)
      })
      .catch(function (error) {
        console.log(error)
        errorCb(error)
      })
  },

  readNewsBoard (newsBoardId, cb, errorCb) {
    const url = urlNewsBoards + '/' + newsBoardId
    let queries = {
      start: -1,
      end: -1
    }
    window.axios.get(url, {params: queries})
      .then(function (response) {
        const serializable = response.data
        cb(serializable)
      })
      .catch(function (error) {
        console.log(error)
        errorCb(error)
      })
  },

  createNewsBoard (params, cb, errorCb) {
    let urlParams = new FormData()
    for (let key in params) {
      urlParams.append(key, params[key])
    }
    window.axios.post(urlNewsBoards /** + '/postnews' */, urlParams, {
      headers: {
        'Accept': 'application/json'
      }
    })
      .then(function (response) {
        const serializable = response.data
        cb(serializable)
      })
      .catch(function (error) {
        console.log(error)
        errorCb(error)
      })
  },

  updateNewsBoard (params, cb, errorCb) {
    const url = urlNewsBoards + '/' + params.newsBoardId
    let urlParams = new URLSearchParams()
    for (let key in params) {
      urlParams.append(key, params[key])
    }
    window.axios.put(url, urlParams)
      .then(function (response) {
        const serializable = response.data
        cb(serializable)
      })
      .catch(function (error) {
        console.log(error)
        errorCb(error)
      })
  },

  deleteNewsBoard (newsBoardId, cb, errorCb) {
    const url = urlNewsBoards + '/' + newsBoardId
    window.axios.delete(url)
      .then(function (response) {
        const serializable = response.data
        cb(serializable)
      })
      .catch(function (error) {
        console.log(error)
        errorCb(error)
      })
  },

  downloadNewsBoard (newsBoardId, cb, errorCb) {
    const url = urlNewsBoards + '/' + newsBoardId + '/download'
    window.axios.delete(url)
      .then(function (response) {
        const serializable = response.data
        cb(serializable)
      })
      .catch(function (error) {
        console.log(error)
        errorCb(error)
      })
  },

  copyNewsBoard (params, cb, errorCb) {
    let queries = {
      start: -1,
      end: -1
    }
    const url = urlNewsBoards + '/' + params.userId + '/copy'
    window.axios.get(url, {params: queries})
      .then(function (response) {
        const serializable = response.data
        cb(serializable)
      })
      .catch(function (error) {
        console.log(error)
        errorCb(error)
      })
  }

}

const exportVl = window.themeDisplay ? hasServer : hasClient
export default exportVl
